                      if (await canLaunchUrl(url)) {
                        await launchUrl(url);
                      } else {
                        ScaffoldMessenger.of(context).showSnackBar(
                          const SnackBar(content: Text('Could not open certificate')),
                        );
                      }
                    },
                    child: const Text(
                      'View Certificate',
                      style: TextStyle(color: Colors.blue, decoration: TextDecoration.underline),
                    ),
                  )
                else
                  Text(value),
              ],
            ),
          ),
        ],
