  Widget _buildEmergencyTab() {
    final contacts = _userData!['emergencyContacts'] as List? ?? [];
    if (contacts.isEmpty) {
      return const Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            Icon(Icons.contacts_outlined, size: 64, color: Colors.grey),
