  // Progress indicator
  Widget _buildProgressIndicator() {
    final sections = widget.role == 'caretaker'
        ? ['Personal', 'Address', 'Caretaker', 'Review']
        : ['Personal', 'Address', 'Review'];

    final currentIndex = _currentSection.index;
    final totalSections = sections.length;

    return Column(
      children: [
        // Progress bar with percentage
        Stack(
          children: [
            LinearProgressIndicator(
              value: (currentIndex + 1) / totalSections,
              backgroundColor: Colors.grey.shade200,
              color: Colors.blueAccent,
              borderRadius: BorderRadius.circular(10),
              minHeight: 8,
            ),
            Positioned(
              right: 0,
              child: Text(
                '${((currentIndex + 1) / totalSections * 100).round()}%',
                style: const TextStyle(
                  fontSize: 12,
                  fontWeight: FontWeight.bold,
                  color: Colors.blueAccent,
                ),
              ),
            ),
          ],
        ),
        const SizedBox(height: 20),
        // Section labels
        Row(
          mainAxisAlignment: MainAxisAlignment.spaceBetween,
          children: sections.asMap().entries.map((entry) {
            final index = entry.key;
            final section = entry.value;
            final isActive = index <= currentIndex;
            final isCurrent = index == currentIndex;

            return Expanded(
              child: Column(
                children: [
                  Container(
                    width: 36,
                    height: 36,
                    decoration: BoxDecoration(
                      color: isActive ? Colors.blueAccent : Colors.grey.shade300,
                      shape: BoxShape.circle,
                      border: isCurrent ? Border.all(color: Colors.blueAccent, width: 3) : null,
                    ),
                    child: Center(
                      child: Icon(
                        isActive ? Icons.check : Icons.circle,
                        color: isActive ? Colors.white : Colors.grey.shade600,
                        size: isActive ? 18 : 16,
                      ),
                    ),
                  ),
                  const SizedBox(height: 8),
                  Text(
                    section,
                    textAlign: TextAlign.center,
                    style: TextStyle(
                      fontSize: 12,
                      fontWeight: isCurrent ? FontWeight.bold : FontWeight.normal,
                      color: isCurrent ? Colors.blueAccent : Colors.grey.shade600,
                    ),
                  ),
                ],
              ),
            );
          }).toList(),
        ),
        const SizedBox(height: 24),
      ],
    );
  }

