        onTap: onTap,
        child: InputDecorator(
          decoration: _inputDecoration(
            isRequired ? '$label *' : label, 
            icon
          ).copyWith(
            contentPadding: const EdgeInsets.symmetric(
              horizontal: 16,
              vertical: 18,
            ),
            fillColor: isSelected ? Colors.green.withOpacity(0.1) : Colors.white,
          ),
          child: Row(
