  Widget _buildGenderRadio(UserGender value, String title) {
    return Expanded(
      child: ListTile(
        title: Text(title, style: const TextStyle(fontSize: 14)),
        leading: Radio<UserGender>(
          value: value,
          groupValue: _selectedGender,
