  // Personal Information Section
  Widget _buildPersonalSection() {
    return Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [
        const Text(
          'Personal Information',
          style: TextStyle(
            fontSize: 20,
            fontWeight: FontWeight.bold,
            color: Colors.blueAccent,
          ),
        ),
        const SizedBox(height: 16),
        _buildTextField(nameController, 'Full Name', Icons.person),
        _buildTextField(usernameController, 'Username', Icons.alternate_email),
        _buildTextField(emailController, 'Email', Icons.email),
        _buildTextField(
          passwordController,
          'Password',
          Icons.lock,
          obscureText: true,
        ),
        _buildTextField(
          phoneController,
          'Phone Number',
          Icons.phone,
          keyboardType: TextInputType.phone,
        ),

        // Profile Image Upload
        _buildFilePicker(
          label: 'Profile Image',
          onTap: _pickProfileImage,
          isSelected: _selectedProfileImagePath != null,
          icon: Icons.person_pin,
        ),
        const Padding(
          padding: EdgeInsets.only(bottom: 16),
          child: Text(
            '* If no profile image is selected, a default image will be used',
            style: TextStyle(
              fontSize: 12,
              color: Colors.grey,
              fontStyle: FontStyle.italic,
            ),
          ),
        ),

        // Gender Selection
        const SizedBox(height: 8),
        const Text(
          'Gender:',
          style: TextStyle(fontSize: 16, color: Colors.black87),
        ),
        Row(
          mainAxisAlignment: MainAxisAlignment.start,
          children: [
            _buildGenderRadio(UserGender.male, 'Male'),
            _buildGenderRadio(UserGender.female, 'Female'),
            _buildGenderRadio(UserGender.other, 'Other'),
          ],
        ),

        // Date of Birth Picker
        Padding(
          padding: const EdgeInsets.only(bottom: 16, top: 8),
          child: InkWell(
            onTap: () => _selectDate(context),
            child: InputDecorator(
              decoration: _inputDecoration('Date of Birth', Icons.calendar_today),
              child: Text(
                _selectedDOB == null
                    ? 'Select Date of Birth'
                    : '${_selectedDOB!.day}/${_selectedDOB!.month}/${_selectedDOB!.year}',
                style: const TextStyle(fontSize: 16),
              ),
            ),
          ),
        ),
      ],
